export const initialState = {
    // isLoggedIn: false,
    // signUpData: {},
    // loginData: {},
    me: null,
    logInLoading: false, // 로그인 시도중
    logInDone: false,
    logInError: null,

    logOutLoading: false, // 로그아웃 시도중
    logOutDone: false,
    logOutError: null,
};

export const loginAction = (data) => {
    return {
        type: "LOG_IN",
        data,
    };
};

export const logoutAction = () => {
    return {
        type: "LOG_OUT",
    };
};

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case "LOG_IN":
            return {
                ...state,
                isLoggedIn: true,
                me: action.data,

            };
        case "LOG_OUT":
            return {
                ...state,
                isLoggedIn: false,
            };
        default:
            return state;
    }
};

export default reducer;
