import shortId from 'shortid';


export const initialState = {
    mainPosts: [
        {
            id: 1,
            User: {
                id: 1,
                nickname: 'terecal',
            },
            content: 'sample 게시글 #test',
            Images: [{
                src: 'https://image.shutterstock.com/image-vector/sample-red-square-grunge-stamp-260nw-338250266.jpg',
            }, {
                src: 'https://gimg.gilbut.co.kr/book/BN001958/rn_view_BN001958.jpg',
            }, {
                src: 'https://gimg.gilbut.co.kr/book/BN001998/rn_view_BN001998.jpg',
            }],
            Comments: [{
                User: {
                    nickname: 'nero',
                },
                content: '첫번째 더미 댓글 fisrt ',
            }, {
                User: {
                    nickname: 'hero',
                },
                content: '두번째 더미 댓글 second',
            }]
        },
    ],
    addPostLoading: false,
    addPostDone: false,
    addPostError: null,

    addCommentLoading: false,
    addCommentDone: false,
    addCommentError: null,
};


const dummyPost = (data) => ({
    id: data.id,
    content: data.content,
    User: {
        id: 1,
        nickname: '제로초',
    },
    Images: [],
    Comments: [],
});

export const ADD_POST_REQUEST = 'ADD_POST_REQUEST';
export const ADD_POST_SUCCESS = 'ADD_POST_SUCCESS';
export const ADD_POST_FAILURE = 'ADD_POST_FAILURE';

export const ADD_COMMENT_REQUEST = 'ADD_COMMENT_REQUEST';
export const ADD_COMMENT_SUCCESS = 'ADD_COMMENT_SUCCESS';
export const ADD_COMMENT_FAILURE = 'ADD_COMMENT_FAILURE';

const reducer = (state = initialState, action) => {
    switch (action.type) {
        // case ADD_POST: {
        //     return {
        //         ...state,
        //         mainPosts: [dummyPost, ...state.mainPosts],
        //         postAdded: true,
        //     };
        // }
        case ADD_POST_REQUEST:
            return {
                ...state,
                addPostLoading: true,
                addPostDone: false,
                addPostError: null,
            };
        case ADD_POST_SUCCESS:
            return {
                ...state,
                addPostLoading: false,
                addPostDone: true,
                mainPosts: [dummyPost(action.data), ...state.mainPosts],
            };
        case ADD_POST_FAILURE:
            return {
                ...state,
                addPostLoading: false,
                addPostError: action.error,
            };

        case ADD_COMMENT_REQUEST:
            return {
                ...state,
                addCommentLoading: true,
                addCommentDone: false,
                addCommentError: null,
            };
        // 댓글 추가를 위한 state 갱신 하기
        // 현재 리턴해야할 state 정보는 다음과 같다. <=> initialState[그외, mainPosts:[{},{},{},{}], 댓글 추가 관련 state 변수]
        // how: mainPosts를 먼저 재구성 하고 그다음 ...mainPosts를 이용해 전체를 재구성한뒤 전체를 재구성
        case ADD_COMMENT_SUCCESS:
            // 갱신할 mainPosts 객체 생성 하기
            const mainPosts = [...state.mainPosts]
            // 본문글 정보를 얻기 위한 index 조회 by action.data.postID
            const postIndex = state.mainPosts.findIndex(
                (v) => v.id === action.data.postId
            );
            // 본문글 정보 가져오기 
            const post = { ...state.mainPosts[postIndex] };
            // 원래 댓글 정보 + 새로운 댓글 정보로 댓글 정보 재구성
            const Comments = [...post.Comments, dummyComment(action.data.content),];
            // mainPosts 재구성
            mainPosts[postIndex] = { ...post, Comments };
            // 전체 리턴값 설정 
            return {
                ...state,
                mainPosts,
                addCommentLoading: false,
                addCommentDone: true,
            };
        case ADD_COMMENT_FAILURE:
            return {
                ...state,
                addCommentLoading: false,
                addCommentError: action.error,
            };

        default:
            return state;
    }
};

export default reducer;
