import React, { useState, useCallback } from "react";
import { Form, Input } from "antd";
import Link from "next/link";
import { LoginFormWrapper, LoginButtonWrapper, LoginButton } from './style/LoginFormStyle';
import { useDispatch, useSelector } from 'react-redux';
import { LOG_IN_REQUEST } from '../reducers/user';
import useInput from '../hooks/useInput';


const LoginForm = () => {
    const dispatch = useDispatch();
    const [email, onChangeEmail] = useInput('');
    const [password, setPassword] = useState("");
    const { logInLoading } = useSelector((state) => state.user);

    const onChangeId = useCallback((e) => {
        setId(e.target.value);
    }, []);

    const onChangePassword = useCallback((e) => {
        setPassword(e.target.value);
    }, []);

    const onSubmitForm = useCallback(() => {
        // console.log("id,password : ", id, password);
        // dispatch(loginAction({ id, password }));
        dispatch({
            type: LOG_IN_REQUEST,
            data: { id, password },
        });
    }, [id, password]);

    return (
        <LoginFormWrapper>
            <Form onFinish={onSubmitForm}>
                <div>
                    <label htmlFor="user-id">아이디</label>
                    <br />
                    <Input
                        name="user-id"
                        value={id}
                        onChange={onChangeId}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="user-password">비밀번호</label>
                    <br />
                    <Input
                        name="user-password"
                        value={password}
                        onChange={onChangePassword}
                        required
                    />
                </div>
                <div>
                    <LoginButtonWrapper>
                        {/* <LoginButton type="primary" htmlType="submit" loading={false} > */}
                        <LoginButton type="primary" htmlType="submit" loading={logInLoading} >
                            로그인
                        </LoginButton>
                        <Link href="/signup">
                            <LoginButton type="default">
                                회원 가입
                            </LoginButton>
                        </Link>
                    </LoginButtonWrapper>
                </div>
            </Form>
        </LoginFormWrapper>

    );
};


export default LoginForm;
