export const initialState = {
    // isLoggedIn: false,
    // signUpData: {},
    // loginData: {},
    me: null,
    logInLoading: false, // 로그인 시도중
    logInDone: false,
    logInError: null,

    logOutLoading: false, // 로그아웃 시도중
    logOutDone: false,
    logOutError: null,
};

// export const loginAction = (data) => {
